var myknxhealthconnection = knx.Connection({
	//ipAddr: "192.168.0.30",
	//ipPort: "3671",
	//debug: true,
	//interface: 'eth0',
	//useMulticastTunneling: true,
	//physAddr: process.env.KNX_SOURCEADDRESS,
	handlers: {
		connected: function() {
			function checkdp(dest) {
				var dp = new knx.Datapoint({
					ga: dest.toString(),
					dpt: "DPT1"
				}, myknxhealthconnection);
				dp.read((src, value) => {
					console.log("**** GA %j reports current value: %j", dest.toString(), value);
				});
			}
			checkdp("0/1/1");
		}
	}
});
